"use strict";function delArrarr1(e){for(var t={},r=0;r<e.length;r++)t[e[r]]?t[e[r]]++:t[e[r]]=1;var n=[];for(var r in t)n.push(r);return n}function delArrarr2(e){for(var t=[],r=0;r<e.length;r++)-1==t.indexOf(e[r])&&t.push(e[r]);return t}function createDate(){var e=new Date,t=e.getFullYear(),r=e.getMonth(),n=e.getDate(),a=e.getDay(),o=e.getHours(),u=e.getMinutes(),c=e.getSeconds();switch(a){case 0:a="星期日";break;case 1:a="星期一";break;case 2:a="星期二";break;case 3:a="星期三";break;case 4:a="星期四";break;case 5:a="星期五";break;case 6:a="星期六"}return t+"年"+createZero(r+1)+"月"+createZero(n)+"日 "+a+" "+createZero(o)+":"+createZero(u)+":"+createZero(c)}function createZero(e){return e<10?"0"+e:""+e}function randomNum(e,t){return Math.round(Math.random()*(e-t)+t)}function createColor(){return"#"+randomNum(0,255).toString(16)+randomNum(0,255).toString(16)+randomNum(0,255).toString(16)}function dateCha(e,t){var r=e.getTime()-t.getTime();return"两个时间相差："+Math.abs(parseInt(r/1e3/60/60/24))+"天"+Math.abs(parseInt(r/1e3/60/60)%24)+"小时"+Math.abs(parseInt(r/1e3/60)%60)+"分钟"+Math.abs(parseInt(r/1e3)%60)+"秒钟"}function getStyle(e,t){return e.currentStyle?e.currentStyle[t]:getComputedStyle(e,!1)[t]}function stopBubble(e){e.stopPropagation?e.stopPropagation():e.cancelBubble=!0}function stopDefault(e){e.preventDefault?e.preventDefault():e.returnValue=!1}function addEvent(e,t,r){e.addEventListener?e.addEventListener(t,r):e.attachEvent("on"+t,r)}function removeEvent(e,t,r){e.removeEventListener?e.removeEventListener(t,r):e.detachEvent("on"+t,r)}function setCookie(e,t,r){var n=new Date;n.setDate(n.getDate()+r),document.cookie=e+"="+t+";expires="+n}function removeCookie(e){setCookie(e,1,-1)}function getCookie(e){for(var t=document.cookie.split("; "),r=0;r<t.length;r++)if(t[r].split("=")[0]==e)return t[r].split("=")[1];return""}